import React, { useState, useRef } from "react";
import ReactPlayer from "react-player";
import { Button } from "reactstrap";

export const RibbonDetail = () => {
  const [state, setState] = useState({
    playing: false,
    paused: false,
    duration: 0,
  });
  const [timeDisplayFormat, setTimeDisplayformat] = useState("normal");
  const [snags, setSnags] = useState([]);
  const { playing } = state;
  const { paused } = state;

  //refs
  const playerRef = useRef(null);
  const canvasRef = useRef(null);

  //play pause toggle
  const handlePlayPause = () => {
    setState({ ...state, playing: !state.playing });
  };

  //format time
  const format = (seconds) => {
    if (isNaN(seconds)) {
      return "00:00";
    }

    //set formatting
    const date = new Date(seconds * 1000);
    const hh = date.getUTCHours();
    const mm = date.getUTCMinutes();
    const ss = date.getUTCSeconds().toString().padStart(2, "0");

    if (hh) {
      return `${hh}:${mm.toString().padStart(2, "0")}:${ss}`;
    }
    return `${mm}:${ss}`;
  };

  //get the seconds played
  const handleProgress = (changeState) => {
    if (!state.seeking) {
      setState({ ...state, ...changeState });
      //format seconds
      const time = format(changeState.playedSeconds);
      setTimeDisplayformat(time);
    }
  };

  //add info for snag
  const addSnag = () => {
    const snagCopy = [...snags];
    snagCopy.push({
      time: playerRef.current.getCurrentTime(),
      display: format(playerRef.current.getCurrentTime()),
    });
    setSnags(snagCopy);
  };

  return (
    <>
      <div className="container ">
        <h1 className="text-center">Ribbon Title</h1>
        <div>
          <div className="d-flex justify-content-center">
            <ReactPlayer
              ref={playerRef}
              // muted={true}
              onPause={paused}
              playing={playing}
              onProgress={handleProgress}
              controls={true}
              url="https://www.youtube.com/watch?v=ysz5S6PUM-U"
            />
          </div>
          <div className="text-center m-3">
            <Button
              className="btn btn-lg btn-secondary w-50"
              onClick={() => {
                handlePlayPause();
                addSnag();
              }}
            >
              {playing ? `Add Snag ${timeDisplayFormat}` : "Continue"}
            </Button>
          </div>
          {/* Snags */}
          <div className="row p-5">
            <div className="col align-self-center">
              <div class="list-group">
                <div class="list-group-item list-group-item-action active">
                  Ribbon Snags
                </div>
                {snags.map((snag) => (
                  <>
                    <div class="list-group-item list-group-item-actions">
                      snag at{" "}
                      <Button className="btn btn-link">{snag.display}</Button>
                    </div>
                  </>
                ))}
                ;
              </div>
            </div>
          </div>
        </div>
        <canvas ref={canvasRef} />
      </div>
    </>
  );
};
